import React from "react";
import { motion } from "framer-motion";

const LiquidGlassCard = ({ children, className = "", hover = true, ...props }) => (
  <motion.div
    className={`relative group overflow-hidden rounded-3xl backdrop-blur-2xl bg-white/10 border border-white/20 shadow-2xl shadow-black/10 before:absolute before:inset-0 before:bg-gradient-to-br before:from-white/30 before:via-white/10 before:to-transparent before:opacity-0 before:transition-opacity before:duration-500 after:absolute after:inset-0 after:bg-gradient-to-t after:from-black/5 after:to-transparent after:pointer-events-none ${hover ? "hover:before:opacity-100 hover:shadow-3xl hover:shadow-black/20" : ""} ${className}`}
    whileHover={hover ? { 
      scale: 1.02, 
      y: -8, 
      transition: { type: "spring", stiffness: 300, damping: 30 } 
    } : {}}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardHeader = ({ children, className = "", ...props }) => (
  <div className={`flex flex-col space-y-1.5 p-6 ${className}`} {...props}>
    {children}
  </div>
);

const LiquidGlassCardTitle = ({ children, className = "", ...props }) => (
  <h3 className={`text-2xl font-semibold leading-none tracking-tight ${className}`} {...props}>
    {children}
  </h3>
);

const LiquidGlassCardDescription = ({ children, className = "", ...props }) => (
  <p className={`text-sm text-muted-foreground ${className}`} {...props}>
    {children}
  </p>
);

const LiquidGlassCardContent = ({ children, className = "", ...props }) => (
  <div className={`p-6 pt-0 ${className}`} {...props}>
    {children}
  </div>
);

const LiquidGlassCardFooter = ({ children, className = "", ...props }) => (
  <div className={`flex items-center p-6 pt-0 ${className}`} {...props}>
    {children}
  </div>
);

export { 
  LiquidGlassCard, 
  LiquidGlassCardHeader, 
  LiquidGlassCardFooter, 
  LiquidGlassCardTitle, 
  LiquidGlassCardDescription, 
  LiquidGlassCardContent 
};
