import React from "react";
import { motion } from "framer-motion";

const LiquidGlassCard = ({ children, className = "", hover = true, ...props }) => (
  <motion.div
    className={`relative group overflow-hidden rounded-3xl backdrop-blur-2xl bg-white/10 border border-white/20 shadow-2xl shadow-black/10 
      before:absolute before:inset-0 before:bg-gradient-to-br before:from-white/30 before:via-white/10 before:to-transparent before:opacity-0 before:transition-all before:duration-500 
      after:absolute after:inset-0 after:bg-gradient-to-t after:from-black/5 after:to-transparent after:pointer-events-none after:opacity-0 after:transition-all after:duration-500
      ${hover ? "hover:before:opacity-100 hover:after:opacity-100 hover:shadow-3xl hover:shadow-black/30 hover:border-white/40 hover:bg-white/15" : ""} 
      transition-all duration-500 ease-out ${className}`}
    whileHover={hover ? { 
      scale: 1.03, 
      y: -12, 
      rotateX: 2,
      rotateY: 1,
      transition: { 
        type: "spring", 
        stiffness: 400, 
        damping: 25,
        mass: 0.8
      } 
    } : {}}
    whileTap={hover ? { 
      scale: 0.98, 
      y: -6,
      transition: { 
        type: "spring", 
        stiffness: 600, 
        damping: 30 
      } 
    } : {}}
    initial={{ opacity: 0, y: 20 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.6, ease: "easeOut" }}
    {...props}
  >
    {/* Animated background gradient */}
    <motion.div
      className="absolute inset-0 bg-gradient-to-br from-indigo-500/10 via-purple-500/10 to-pink-500/10 opacity-0"
      whileHover={{ opacity: 1 }}
      transition={{ duration: 0.3 }}
    />
    
    {/* Shimmer effect */}
    <motion.div
      className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -skew-x-12 opacity-0"
      whileHover={{
        x: ["-100%", "100%"],
        opacity: [0, 1, 0],
        transition: { duration: 0.8, ease: "easeInOut" }
      }}
    />
    
    <div className="relative z-10">
      {children}
    </div>
  </motion.div>
);

const LiquidGlassCardHeader = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex flex-col space-y-1.5 p-6 ${className}`} 
    whileHover={{ y: -2 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardTitle = ({ children, className = "", ...props }) => (
  <motion.h3 
    className={`text-2xl font-semibold leading-none tracking-tight ${className}`} 
    whileHover={{ scale: 1.02 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.h3>
);

const LiquidGlassCardDescription = ({ children, className = "", ...props }) => (
  <motion.p 
    className={`text-sm text-muted-foreground ${className}`} 
    whileHover={{ y: -1 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.p>
);

const LiquidGlassCardContent = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`p-6 pt-0 ${className}`} 
    whileHover={{ y: -2 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardFooter = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex items-center p-6 pt-0 ${className}`} 
    whileHover={{ y: -2 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

export { 
  LiquidGlassCard, 
  LiquidGlassCardHeader, 
  LiquidGlassCardFooter, 
  LiquidGlassCardTitle, 
  LiquidGlassCardDescription, 
  LiquidGlassCardContent 
};
