import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [theme, setTheme] = useState(() => {
    // Check localStorage first, then default to 'dark'
    const savedTheme = localStorage.getItem('theme');
    return savedTheme || 'dark';
  });

  useEffect(() => {
    // Save theme to localStorage
    localStorage.setItem('theme', theme);
    
    // Apply theme to document and set CSS variables
    const root = document.documentElement;
    
    if (theme === 'dark') {
      root.classList.add('dark');
      root.classList.remove('light');
      // Set dark theme colors
      root.style.setProperty('--content-color', '#0C0A3E');
      root.style.setProperty('--bg-color', '#0a0a0a');
      root.style.setProperty('--text-primary', '#ffffff');
      root.style.setProperty('--text-secondary', '#ffffff80');
      root.style.setProperty('--card-bg', 'rgba(255, 255, 255, 0.1)');
      root.style.setProperty('--border-color', 'rgba(255, 255, 255, 0.2)');
    } else {
      root.classList.add('light');
      root.classList.remove('dark');
      // Set light theme colors
      root.style.setProperty('--content-color', '#f6f6f6');
      root.style.setProperty('--bg-color', '#ffffff');
      root.style.setProperty('--text-primary', '#0C0A3E');
      root.style.setProperty('--text-secondary', '#0C0A3E80');
      root.style.setProperty('--card-bg', 'rgba(12, 10, 62, 0.1)');
      root.style.setProperty('--border-color', 'rgba(12, 10, 62, 0.2)');
    }
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prevTheme => prevTheme === 'dark' ? 'light' : 'dark');
  };

  const value = {
    theme,
    toggleTheme,
    isDark: theme === 'dark',
    isLight: theme === 'light',
    colors: {
      content: theme === 'dark' ? '#0C0A3E' : '#f6f6f6',
      background: theme === 'dark' ? '#0a0a0a' : '#ffffff',
      textPrimary: theme === 'dark' ? '#ffffff' : '#0C0A3E',
      textSecondary: theme === 'dark' ? '#ffffff80' : '#0C0A3E80',
      cardBg: theme === 'dark' ? 'rgba(255, 255, 255, 0.1)' : 'rgba(12, 10, 62, 0.1)',
      border: theme === 'dark' ? 'rgba(255, 255, 255, 0.2)' : 'rgba(12, 10, 62, 0.2)'
    }
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
};
