import React, { useState, useEffect } from "react";
import axios from "axios";
import { GlassCard, GlassCardHeader, GlassCardContent, GlassCardTitle, GlassCardDescription } from "../components/ui/glass-card";
import { Badge } from "../components/ui/badge";
import { FadeIn, ScaleIn, StaggerContainer, StaggerItem } from "../components/ScrollAnimations";
import { Calendar, ExternalLink, Filter } from "lucide-react";

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/projects`);
      setProjects(response.data);
      
      // Extract unique categories
      const uniqueCategories = [...new Set(response.data.map(project => project.category))];
      setCategories(uniqueCategories);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setLoading(false);
    }
  };

  const filteredProjects = selectedCategory === "all" 
    ? projects 
    : projects.filter(project => project.category === selectedCategory);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-slate-900"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Hero Section */}
        <FadeIn>
          <div className="text-center space-y-6 mb-20">
            <Badge variant="secondary" className="px-4 py-2 text-sm backdrop-blur-xl bg-white/20">
              Our Portfolio
            </Badge>
            <h1 className="text-4xl md:text-5xl font-bold text-slate-900">
              Our <span className="text-slate-600">Projects</span>
            </h1>
            <p className="text-xl text-slate-600 max-w-3xl mx-auto leading-relaxed">
              Discover our latest work and successful implementations. Each project represents 
              our commitment to excellence and innovation in IT solutions.
            </p>
          </div>
        </FadeIn>

        {/* Category Filter */}
        <FadeIn delay={0.2}>
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            <button
              onClick={() => setSelectedCategory("all")}
              className={`px-6 py-3 rounded-full transition-all duration-300 backdrop-blur-xl border ${
                selectedCategory === "all" 
                  ? "bg-slate-900/20 border-slate-900/30 text-slate-900" 
                  : "bg-white/10 border-white/20 text-slate-700 hover:bg-white/20"
              }`}
            >
              All Projects
            </button>
            {categories.map((category) => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-6 py-3 rounded-full transition-all duration-300 backdrop-blur-xl border ${
                  selectedCategory === category 
                    ? "bg-slate-900/20 border-slate-900/30 text-slate-900" 
                    : "bg-white/10 border-white/20 text-slate-700 hover:bg-white/20"
                }`}
              >
                {category}
              </button>
            ))}
          </div>
        </FadeIn>

        {/* Projects Grid */}
        <StaggerContainer>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredProjects.map((project, index) => (
              <StaggerItem key={project.id}>
                <GlassCard className="group hover:shadow-2xl transition-all duration-500 hover:-translate-y-2">
                  {project.featured_image && (
                    <div className="relative overflow-hidden rounded-t-2xl">
                      <img
                        src={`${process.env.REACT_APP_BACKEND_URL}${project.featured_image}`}
                        alt={project.title}
                        className="w-full h-48 object-cover transition-transform duration-500 group-hover:scale-110"
                        onError={(e) => {
                          e.target.src = "https://images.unsplash.com/photo-1551288049-bebda4e38f71?w=600&h=300&fit=crop";
                        }}
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />
                      {project.is_featured && (
                        <Badge className="absolute top-4 right-4 bg-blue-600/80 text-white">
                          Featured
                        </Badge>
                      )}
                    </div>
                  )}
                  
                  <GlassCardHeader>
                    <div className="space-y-3">
                      <Badge variant="outline" className="w-fit">
                        {project.category}
                      </Badge>
                      <GlassCardTitle className="group-hover:text-slate-700 transition-colors">
                        {project.title}
                      </GlassCardTitle>
                      <GlassCardDescription>
                        {project.description}
                      </GlassCardDescription>
                    </div>
                  </GlassCardHeader>
                  
                  <GlassCardContent>
                    <div className="space-y-4">
                      <div className="flex items-center justify-between text-sm text-slate-600">
                        <span className="font-medium">Client: {project.client}</span>
                        <div className="flex items-center space-x-1">
                          <Calendar className="h-4 w-4" />
                          <span>{new Date(project.completion_date).toLocaleDateString()}</span>
                        </div>
                      </div>
                      
                      {project.tags && project.tags.length > 0 && (
                        <div className="flex flex-wrap gap-2">
                          {project.tags.map((tag, tagIndex) => (
                            <Badge key={tagIndex} variant="secondary" className="text-xs">
                              {tag}
                            </Badge>
                          ))}
                        </div>
                      )}
                    </div>
                  </GlassCardContent>
                </GlassCard>
              </StaggerItem>
            ))}
          </div>
        </StaggerContainer>

        {filteredProjects.length === 0 && (
          <FadeIn>
            <div className="text-center py-20">
              <div className="w-24 h-24 mx-auto mb-6 bg-slate-100 rounded-full flex items-center justify-center">
                <Filter className="h-12 w-12 text-slate-400" />
              </div>
              <h3 className="text-xl font-semibold text-slate-900 mb-2">No Projects Found</h3>
              <p className="text-slate-600">
                {selectedCategory === "all" 
                  ? "We're working on adding our latest projects. Check back soon!" 
                  : `No projects found in the "${selectedCategory}" category.`}
              </p>
            </div>
          </FadeIn>
        )}
      </div>
    </div>
  );
};

export default Projects;
