import React, { useState, useEffect } from "react"; 
import axios from "axios"; 
import { LiquidGlassCard, LiquidGlassCardHeader, LiquidGlassCardContent, LiquidGlassCardTitle, LiquidGlassCardDescription } from "../components/ui/liquid-glass"; 
import { LiquidGlassButton } from "../components/ui/liquid-glass-button"; 
import { Badge } from "../components/ui/badge";
import { FadeIn, ScaleIn, StaggerContainer, StaggerItem } from "../components/ScrollAnimations";
import { Calendar, ExternalLink, Filter } from "lucide-react";

const Projects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/projects`);
      setProjects(response.data);
      
      // Extract unique categories
      const uniqueCategories = [...new Set(response.data.map(project => project.category))];
      setCategories(uniqueCategories);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setLoading(false);
    }
  };

  const filteredProjects = selectedCategory === "all" 
    ? projects 
    : projects.filter(project => project.category === selectedCategory);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen py-20 px-4">
      <div className="max-w-7xl mx-auto">
        <FadeIn>
          <div className="text-center mb-16">
            <h1 className="text-5xl font-bold text-white mb-6">
              Our <span className="bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">Projects</span>
            </h1>
            <p className="text-xl text-white/70 max-w-3xl mx-auto">
              Explore our portfolio of successful projects and see how we've helped businesses transform their digital presence.
            </p>
          </div>
        </FadeIn>

        {/* Category Filter */}
        <StaggerContainer>
          <div className="flex flex-wrap justify-center gap-4 mb-12">
            <StaggerItem>
              <Badge variant="secondary" className="px-4 py-2 text-sm backdrop-blur-xl bg-white/30 border-white/40">
                <Filter className="w-4 h-4 mr-2" />
                Filter by Category
              </Badge>
            </StaggerItem>
            
            <StaggerItem>
              <button
                onClick={() => setSelectedCategory("all")}
                className={`px-6 py-2 rounded-full text-sm font-medium transition-all duration-300 ${
                  selectedCategory === "all"
                    ? "bg-white/20 text-white border border-white/30"
                    : "text-white/70 hover:bg-white/10 hover:text-white"
                }`}
              >
                All Projects
              </button>
            </StaggerItem>
            
            {categories.map((category, index) => (
              <StaggerItem key={category}>
                <button
                  onClick={() => setSelectedCategory(category)}
                  className={`px-6 py-2 rounded-full text-sm font-medium transition-all duration-300 ${
                    selectedCategory === category
                      ? "bg-white/20 text-white border border-white/30"
                      : "text-white/70 hover:bg-white/10 hover:text-white"
                  }`}
                >
                  {category}
                </button>
              </StaggerItem>
            ))}
          </div>
        </StaggerContainer>

        {/* Projects Grid */}
        <StaggerContainer>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredProjects.map((project, index) => (
              <StaggerItem key={project.id}>
                <ScaleIn delay={index * 0.1}>
                  <LiquidGlassCard className="group hover:shadow-2xl transition-all duration-500 hover:-translate-y-2">
                    <div className="relative overflow-hidden rounded-t-2xl">
                      <img
                        src={project.image_url || '/api/placeholder/400/300'}
                        alt={project.title}
                        className="w-full h-48 object-cover transition-transform duration-500 group-hover:scale-110"
                      />
                      <Badge className="absolute top-4 right-4 bg-blue-600/80 text-white">
                        {project.category}
                      </Badge>
                    </div>
                    
                    <LiquidGlassCardHeader>
                      <LiquidGlassCardTitle className="text-xl text-white mb-2">
                        {project.title}
                      </LiquidGlassCardTitle>
                      <LiquidGlassCardDescription className="text-white/70 mb-4">
                        {project.description}
                      </LiquidGlassCardDescription>
                      
                      <div className="flex items-center text-white/60 text-sm mb-4">
                        <Calendar className="w-4 h-4 mr-2" />
                        {new Date(project.created_at).toLocaleDateString()}
                      </div>
                      
                      <Badge variant="outline" className="w-fit text-white/80 border-white/30">
                        {project.status}
                      </Badge>
                    </LiquidGlassCardHeader>
                    
                    <LiquidGlassCardContent>
                      <div className="flex flex-wrap gap-2 mb-4">
                        {project.technologies?.map((tech, tagIndex) => (
                          <Badge key={tagIndex} variant="secondary" className="text-xs text-white/80 bg-white/10 border-white/20">
                            {tech}
                          </Badge>
                        ))}
                      </div>
                      
                      <div className="flex gap-3">
                        <LiquidGlassButton 
                          className="flex-1 bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600"
                          onClick={() => window.open(project.demo_url, '_blank')}
                        >
                          <ExternalLink className="w-4 h-4 mr-2" />
                          View Demo
                        </LiquidGlassButton>
                      </div>
                    </LiquidGlassCardContent>
                  </LiquidGlassCard>
                </ScaleIn>
              </StaggerItem>
            ))}
          </div>
        </StaggerContainer>

        {filteredProjects.length === 0 && (
          <div className="text-center py-16">
            <p className="text-white/70 text-lg">No projects found for the selected category.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Projects;
