import React, { useState, useRef } from "react";

const LiquidGlassCard = React.forwardRef(({ className, children, ...props }, ref) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;

    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setMousePosition({ x, y });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setMousePosition({ x: 0, y: 0 });
  };

  // Calculate subtle rotation
  const rotateX = isHovered ? (mousePosition.y - 150) / 20 : 0;
  const rotateY = isHovered ? (mousePosition.x - 200) / 20 : 0;

  return (
    <div
      ref={cardRef}
      className={`relative overflow-hidden rounded-2xl backdrop-blur-xl border shadow-2xl shadow-indigo-500/10 transition-all duration-300 ease-out ${className || ''}`}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`,
        background: isHovered 
          ? `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.05) 50%, transparent 70%)`
          : 'rgba(255, 255, 255, 0.1)',
        borderColor: isHovered ? 'rgba(255, 255, 255, 0.4)' : 'rgba(255, 255, 255, 0.2)',
      }}
      {...props}
    >
      {/* Subtle highlight that follows cursor */}
      <div
        className="absolute inset-0 transition-opacity duration-200"
        style={{
          opacity: isHovered ? 1 : 0,
          background: `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.2) 0%, transparent 60%)`,
        }}
      />

      {/* Shimmer effect */}
      <div
        className="absolute inset-0 transition-all duration-500"
        style={{
          opacity: isHovered ? 1 : 0,
          background: `linear-gradient(45deg, transparent 30%, rgba(255, 255, 255, 0.3) 50%, transparent 70%)`,
          transform: `translateX(${isHovered ? mousePosition.x - 200 : -200}px) translateY(${isHovered ? mousePosition.y - 100 : -100}px) rotate(45deg)`,
        }}
      />

      {/* Liquid ripple */}
      <div
        className="absolute rounded-full border border-white/20 transition-all duration-600"
        style={{
          left: mousePosition.x - 15,
          top: mousePosition.y - 15,
          width: '30px',
          height: '30px',
          opacity: isHovered ? [0, 1, 0] : 0,
          transform: `scale(${isHovered ? 2 : 0})`,
        }}
      />

      <div className="relative z-10">
        {children}
      </div>
    </div>
  );
});
LiquidGlassCard.displayName = "LiquidGlassCard";

const LiquidGlassButton = React.forwardRef(({ className, variant = "default", size = "default", children, ...props }, ref) => {
  const baseStyles = "relative overflow-hidden rounded-xl font-medium transition-all duration-300 ease-out focus:outline-none focus:ring-2 focus:ring-indigo-500/50";

  const variants = {
    default: "bg-white/10 backdrop-blur-xl border border-white/20 text-white hover:bg-white/20 hover:border-white/30 shadow-lg shadow-indigo-500/10",
    primary: "bg-gradient-to-r from-indigo-500/20 to-purple-500/20 backdrop-blur-xl border border-indigo-400/30 text-white hover:from-indigo-500/30 hover:to-purple-500/30 shadow-lg shadow-indigo-500/20",
    ghost: "bg-transparent border border-white/10 text-white hover:bg-white/5 hover:border-white/20"
  };

  const sizes = {
    sm: "px-4 py-2 text-sm",
    default: "px-6 py-3 text-base",
    lg: "px-8 py-4 text-lg"
  };

  return (
    <button
      ref={ref}
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} before:absolute before:inset-0 before:bg-gradient-to-br before:from-white/10 before:to-transparent before:opacity-0 hover:before:opacity-100 before:transition-opacity before:duration-300 ${className || ''}`}
      {...props}
    >
      <span className="relative z-10">{children}</span>
    </button>
  );
});
LiquidGlassButton.displayName = "LiquidGlassButton";

export { LiquidGlassCard, LiquidGlassButton };
