import React, { useState, useRef } from "react";
import { motion } from "framer-motion";

const LiquidGlassCard = ({ children, className = "", hover = true, ...props }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;
    
    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    setMousePosition({ x, y });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setMousePosition({ x: 0, y: 0 });
  };

  // Calculate transform values for 3D effect
  const rotateX = isHovered ? (mousePosition.y - 150) / 8 : 0;
  const rotateY = isHovered ? (mousePosition.x - 200) / 8 : 0;

  return (
    <motion.div
      ref={cardRef}
      className={`relative group overflow-hidden rounded-3xl backdrop-blur-2xl bg-white/10 border border-white/20 shadow-2xl shadow-black/10 
        before:absolute before:inset-0 before:bg-gradient-to-br before:from-white/30 before:via-white/10 before:to-transparent before:opacity-0 before:transition-all before:duration-500 
        after:absolute after:inset-0 after:bg-gradient-to-t after:from-black/5 after:to-transparent after:pointer-events-none after:opacity-0 after:transition-all after:duration-500
        ${hover ? "hover:before:opacity-100 hover:after:opacity-100 hover:shadow-3xl hover:shadow-black/30 hover:border-white/40 hover:bg-white/15" : ""} 
        transition-all duration-500 ease-out ${className}`}
      whileHover={hover ? { 
        scale: 1.05, 
        y: -15, 
        transition: { 
          type: "spring", 
          stiffness: 300, 
          damping: 20
        } 
      } : {}}
      whileTap={hover ? { 
        scale: 0.95, 
        y: -5,
        transition: { 
          type: "spring", 
          stiffness: 400, 
          damping: 25 
        } 
      } : {}}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`
      }}
      {...props}
    >
      {/* Dramatic mouse-following liquid glass effect */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 1 : 0,
          background: isHovered ? 
            `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(99, 102, 241, 0.4) 0%, rgba(168, 85, 247, 0.3) 30%, rgba(236, 72, 153, 0.2) 60%, transparent 80%)` :
            "transparent"
        }}
        transition={{ duration: 0.1 }}
      />
      
      {/* Bright shimmer effect that follows mouse */}
      <motion.div
        className="absolute inset-0 bg-gradient-to-r from-transparent via-white/50 to-transparent -skew-x-12 opacity-0"
        animate={{
          x: isHovered ? mousePosition.x - 150 : -150,
          y: isHovered ? mousePosition.y - 75 : 0,
          opacity: isHovered ? [0, 1, 0] : 0,
        }}
        transition={{ duration: 0.8, ease: "easeInOut" }}
      />
      
      {/* Large bright highlight that follows cursor */}
      <motion.div
        className="absolute w-48 h-48 bg-white/30 rounded-full blur-2xl opacity-0"
        animate={{
          x: mousePosition.x - 96,
          y: mousePosition.y - 96,
          opacity: isHovered ? 0.8 : 0,
          scale: isHovered ? [0.5, 1.5, 0.5] : 0.5,
        }}
        transition={{ duration: 0.2, ease: "easeOut" }}
      />
      
      {/* Multiple ripple effects */}
      <motion.div
        className="absolute w-24 h-24 border-2 border-white/60 rounded-full opacity-0"
        animate={{
          x: mousePosition.x - 48,
          y: mousePosition.y - 48,
          opacity: isHovered ? [0, 1, 0] : 0,
          scale: isHovered ? [0, 2, 3] : 0,
        }}
        transition={{ duration: 1, ease: "easeOut" }}
      />
      
      <motion.div
        className="absolute w-32 h-32 border border-white/40 rounded-full opacity-0"
        animate={{
          x: mousePosition.x - 64,
          y: mousePosition.y - 64,
          opacity: isHovered ? [0, 0.7, 0] : 0,
          scale: isHovered ? [0, 1.5, 2.5] : 0,
        }}
        transition={{ duration: 1.2, ease: "easeOut", delay: 0.1 }}
      />
      
      {/* Liquid distortion effect */}
      <motion.div
        className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent opacity-0"
        animate={{
          x: mousePosition.x - 300,
          y: mousePosition.y - 150,
          opacity: isHovered ? [0, 0.8, 0] : 0,
          rotate: isHovered ? [0, 10, 0] : 0,
        }}
        transition={{ duration: 0.6, ease: "easeInOut" }}
      />
      
      {/* Color-changing background */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 1 : 0,
          background: isHovered ? [
            `linear-gradient(45deg, rgba(99, 102, 241, 0.1), rgba(168, 85, 247, 0.1), rgba(236, 72, 153, 0.1))`,
            `linear-gradient(135deg, rgba(168, 85, 247, 0.1), rgba(236, 72, 153, 0.1), rgba(99, 102, 241, 0.1))`,
            `linear-gradient(225deg, rgba(236, 72, 153, 0.1), rgba(99, 102, 241, 0.1), rgba(168, 85, 247, 0.1))`
          ] : "transparent"
        }}
        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
      />
      
      <div className="relative z-10">
        {children}
      </div>
    </motion.div>
  );
};

const LiquidGlassCardHeader = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex flex-col space-y-1.5 p-6 ${className}`} 
    whileHover={{ y: -3, scale: 1.01 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardTitle = ({ children, className = "", ...props }) => (
  <motion.h3 
    className={`text-2xl font-semibold leading-none tracking-tight ${className}`} 
    whileHover={{ scale: 1.03, y: -2 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.h3>
);

const LiquidGlassCardDescription = ({ children, className = "", ...props }) => (
  <motion.p 
    className={`text-sm text-muted-foreground ${className}`} 
    whileHover={{ y: -2, scale: 1.01 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.p>
);

const LiquidGlassCardContent = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`p-6 pt-0 ${className}`} 
    whileHover={{ y: -3, scale: 1.01 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardFooter = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex items-center p-6 pt-0 ${className}`} 
    whileHover={{ y: -3, scale: 1.01 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

export { 
  LiquidGlassCard, 
  LiquidGlassCardHeader, 
  LiquidGlassCardFooter, 
  LiquidGlassCardTitle, 
  LiquidGlassCardDescription, 
  LiquidGlassCardContent 
};
