import React, { useState, useEffect } from "react";
import axios from "axios";
import { LiquidGlassCard, LiquidGlassCardHeader, LiquidGlassCardContent } from "../components/ui/liquid-glass";
import { AppleRevealText, BikeBearText } from "../components/BikeBearAnimations";
import { FadeIn, StaggerContainer, StaggerItem, ScaleIn } from "../components/ScrollAnimations";
import { Badge } from "../components/ui/badge";
import { Star, Quote } from "lucide-react";

const Testimonials = () => {
  const [testimonials, setTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTestimonials();
  }, []);

  const fetchTestimonials = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/testimonials`);
      setTestimonials(response.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching testimonials:", error);
      setTestimonials([
        { id: 1, name: "Ahmad Rahman", role: "Small Business Owner", company: "Rahman Trading", content: "Netrik Techworks saved my business laptop! Their service was quick, affordable, and professional. The team went above and beyond to recover my important data.", rating: 5, image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100&h=100&fit=crop&crop=face", is_featured: true },
        { id: 2, name: "Sarah Lee", role: "University Student", company: "Universiti Malaya", content: "Got my laptop serviced for just RM40. The performance improvement was incredible. Great value for money and excellent customer service!", rating: 5, image: "https://images.unsplash.com/photo-1494790108755-2616b612b47c?w=100&h=100&fit=crop&crop=face", is_featured: true },
        { id: 3, name: "David Tan", role: "IT Manager", company: "Tech Solutions Sdn Bhd", content: "Professional service and competitive prices. They handled our office laptop upgrades seamlessly and provided excellent after-sales support.", rating: 5, image: "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face", is_featured: false }
      ]);
      setLoading(false);
    }
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star key={i} className={`w-4 h-4 ${i < rating ? "text-yellow-400 fill-yellow-400" : "text-gray-300"}`} />
    ));
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
      </div>
    );
  }

  const featuredTestimonials = testimonials.filter(t => t.is_featured).slice(0, 3);
  const regularTestimonials = testimonials.filter(t => !t.is_featured);

  return (
    <div className="min-h-screen py-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <AppleRevealText>
          <div className="text-center space-y-6 mb-20">
            <Badge variant="secondary" className="px-4 py-2 text-sm backdrop-blur-xl bg-white/30 border-white/40">
              Client Reviews
            </Badge>
            <h1 className="text-4xl md:text-5xl font-bold text-white">
              What Our <span className="bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">Clients Say</span>
            </h1>
            <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
              Don't just take our word for it. Read what our satisfied customers have to say about our IT services and support.
            </p>
          </div>
        </AppleRevealText>

        {featuredTestimonials.length > 0 && (
          <div className="mb-20">
            <AppleRevealText delay={0.2}>
              <h2 className="text-2xl font-bold text-white text-center mb-12">Featured Reviews</h2>
            </AppleRevealText>
            <StaggerContainer>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
                {featuredTestimonials.map((testimonial, index) => (
                  <StaggerItem key={testimonial.id}>
                    <ScaleIn delay={index * 0.1}>
                      <LiquidGlassCard className="group hover:shadow-2xl transition-all duration-500 hover:-translate-y-3 h-full">
                        <LiquidGlassCardHeader className="text-center">
                          <div className="relative">
                            <div className="absolute -top-4 -left-4 text-white/30">
                              <Quote className="w-8 h-8" />
                            </div>
                            <div className="w-20 h-20 mx-auto mb-4 relative">
                              <img 
                                src={testimonial.image} 
                                alt={testimonial.name} 
                                className="w-full h-full rounded-full object-cover border-4 border-white/30" 
                                onError={(e) => { 
                                  e.target.src = "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100&h=100&fit=crop&crop=face"; 
                                }} 
                              />
                              <Badge className="absolute -top-2 -right-2 bg-blue-600/80 text-white text-xs px-2 py-1">
                                Featured
                              </Badge>
                            </div>
                            <div className="space-y-2">
                              <h3 className="text-lg font-semibold text-white">{testimonial.name}</h3>
                              <p className="text-sm text-white/70">
                                {testimonial.role}
                                {testimonial.company && (
                                  <span className="block text-xs text-white/60 mt-1">{testimonial.company}</span>
                                )}
                              </p>
                              <div className="flex justify-center space-x-1">
                                {renderStars(testimonial.rating)}
                              </div>
                            </div>
                          </div>
                        </LiquidGlassCardHeader>
                        <LiquidGlassCardContent>
                          <p className="text-white/80 text-center italic leading-relaxed">
                            "{testimonial.content}"
                          </p>
                        </LiquidGlassCardContent>
                      </LiquidGlassCard>
                    </ScaleIn>
                  </StaggerItem>
                ))}
              </div>
            </StaggerContainer>
          </div>
        )}

        {regularTestimonials.length > 0 && (
          <div>
            <AppleRevealText delay={0.4}>
              <h2 className="text-2xl font-bold text-white text-center mb-12">More Client Reviews</h2>
            </AppleRevealText>
            <StaggerContainer>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {regularTestimonials.map((testimonial) => (
                  <StaggerItem key={testimonial.id}>
                    <ScaleIn>
                      <LiquidGlassCard className="group hover:shadow-xl transition-all duration-300 hover:-translate-y-2">
                        <LiquidGlassCardContent className="p-6">
                          <div className="flex items-start space-x-4">
                            <div className="flex-shrink-0">
                              <img 
                                src={testimonial.image} 
                                alt={testimonial.name} 
                                className="w-16 h-16 rounded-full object-cover border-2 border-white/30" 
                                onError={(e) => { 
                                  e.target.src = "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=100&h=100&fit=crop&crop=face"; 
                                }} 
                              />
                            </div>
                            <div className="flex-1">
                              <div className="flex items-center justify-between mb-2">
                                <div>
                                  <h3 className="font-semibold text-white">{testimonial.name}</h3>
                                  <p className="text-sm text-white/70">
                                    {testimonial.role}
                                    {testimonial.company && (
                                      <span className="block text-xs text-white/60">{testimonial.company}</span>
                                    )}
                                  </p>
                                </div>
                                <div className="flex space-x-1">
                                  {renderStars(testimonial.rating)}
                                </div>
                              </div>
                              <p className="text-white/80 italic">"{testimonial.content}"</p>
                            </div>
                          </div>
                        </LiquidGlassCardContent>
                      </LiquidGlassCard>
                    </ScaleIn>
                  </StaggerItem>
                ))}
              </div>
            </StaggerContainer>
          </div>
        )}

        {testimonials.length === 0 && (
          <AppleRevealText>
            <div className="text-center py-20">
              <div className="w-24 h-24 mx-auto mb-6 bg-white/10 rounded-full flex items-center justify-center">
                <Quote className="h-12 w-12 text-white/40" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-2">No Testimonials Yet</h3>
              <p className="text-white/70">We're gathering feedback from our amazing clients. Check back soon!</p>
            </div>
          </AppleRevealText>
        )}
      </div>
    </div>
  );
};

export default Testimonials;
