import React, { useEffect, useRef, useState } from "react";

const CursorReactiveBackground = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const dots = [];
    const dotCount = 2000;
    const dotSize = 2;
    const maxDistance = 150;

    // Create dots
    for (let i = 0; i < dotCount; i++) {
      dots.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        originalX: 0,
        originalY: 0,
        size: dotSize,
        originalSize: dotSize,
        opacity: 0.3,
        originalOpacity: 0.3,
        velocity: {
          x: (Math.random() - 0.5) * 0.5,
          y: (Math.random() - 0.5) * 0.5
        }
      });
    }

    // Store original positions
    dots.forEach(dot => {
      dot.originalX = dot.x;
      dot.originalY = dot.y;
    });

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      dots.forEach(dot => {
        // Calculate distance from mouse
        const dx = mousePosition.x - dot.x;
        const dy = mousePosition.y - dot.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < maxDistance && isHovering) {
          // Repel effect
          const force = (maxDistance - distance) / maxDistance;
          const angle = Math.atan2(dy, dx);
          
          dot.x -= Math.cos(angle) * force * 3;
          dot.y -= Math.sin(angle) * force * 3;
          
          // Scale and opacity effects
          dot.size = dot.originalSize + force * 3;
          dot.opacity = dot.originalOpacity + force * 0.7;
        } else {
          // Return to original position
          dot.x += (dot.originalX - dot.x) * 0.05;
          dot.y += (dot.originalY - dot.y) * 0.05;
          dot.size += (dot.originalSize - dot.size) * 0.05;
          dot.opacity += (dot.originalOpacity - dot.opacity) * 0.05;
        }

        // Add subtle floating motion
        dot.x += dot.velocity.x;
        dot.y += dot.velocity.y;

        // Bounce off edges
        if (dot.x < 0 || dot.x > canvas.width) dot.velocity.x *= -1;
        if (dot.y < 0 || dot.y > canvas.height) dot.velocity.y *= -1;

        // Keep dots in bounds
        dot.x = Math.max(0, Math.min(canvas.width, dot.x));
        dot.y = Math.max(0, Math.min(canvas.height, dot.y));

        // Draw dot
        ctx.beginPath();
        ctx.arc(dot.x, dot.y, dot.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${dot.opacity})`;
        ctx.fill();
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [mousePosition, isHovering]);

  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    setMousePosition({
      x: e.clientX - rect.left,
      y: e.clientY - rect.top
    });
  };

  const handleMouseEnter = () => {
    setIsHovering(true);
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  const resizeCanvas = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
  };

  useEffect(() => {
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);
    return () => window.removeEventListener('resize', resizeCanvas);
  }, []);

  return (
    <div 
      className="fixed inset-0 pointer-events-none z-0"
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <canvas
        ref={canvasRef}
        className="w-full h-full"
        style={{ background: 'transparent' }}
      />
    </div>
  );
};

export default CursorReactiveBackground;
