import React, { useState, useRef } from "react";
import { motion } from "framer-motion";

const LiquidGlassCard = ({ children, className = "", hover = true, ...props }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;
    
    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    setMousePosition({ x, y });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setMousePosition({ x: 0, y: 0 });
  };

  // Calculate rotation based on mouse position
  const rotateX = isHovered ? (mousePosition.y - 150) / 10 : 0;
  const rotateY = isHovered ? (mousePosition.x - 200) / 10 : 0;

  return (
    <div
      ref={cardRef}
      className={`relative group overflow-hidden rounded-3xl backdrop-blur-xl bg-white/10 border border-white/20 shadow-2xl shadow-black/10 
        transition-all duration-300 ease-out ${className}`}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg)`,
        background: isHovered 
          ? `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.05) 50%, transparent 70%)`
          : 'rgba(255, 255, 255, 0.05)',
      }}
      {...props}
    >
      {/* Mouse-following highlight */}
      <div
        className="absolute inset-0 opacity-0 transition-opacity duration-200"
        style={{
          opacity: isHovered ? 1 : 0,
          background: `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.3) 0%, transparent 60%)`,
        }}
      />
      
      {/* Shimmer effect */}
      <div
        className="absolute inset-0 opacity-0 transition-all duration-500"
        style={{
          opacity: isHovered ? 1 : 0,
          background: `linear-gradient(45deg, transparent 30%, rgba(255, 255, 255, 0.4) 50%, transparent 70%)`,
          transform: `translateX(${isHovered ? mousePosition.x - 200 : -200}px) translateY(${isHovered ? mousePosition.y - 100 : -100}px) rotate(45deg)`,
        }}
      />
      
      {/* Ripple effect */}
      <div
        className="absolute rounded-full border-2 border-white/30 opacity-0 transition-all duration-600"
        style={{
          left: mousePosition.x - 25,
          top: mousePosition.y - 25,
          width: '50px',
          height: '50px',
          opacity: isHovered ? [0, 1, 0] : 0,
          transform: `scale(${isHovered ? 3 : 0})`,
        }}
      />
      
      <div className="relative z-10">
        {children}
      </div>
    </div>
  );
};

const LiquidGlassCardHeader = ({ children, className = "", ...props }) => (
  <div className={`flex flex-col space-y-1.5 p-6 ${className}`} {...props}>
    {children}
  </div>
);

const LiquidGlassCardTitle = ({ children, className = "", ...props }) => (
  <h3 className={`text-2xl font-semibold leading-none tracking-tight ${className}`} {...props}>
    {children}
  </h3>
);

const LiquidGlassCardDescription = ({ children, className = "", ...props }) => (
  <p className={`text-sm text-muted-foreground ${className}`} {...props}>
    {children}
  </p>
);

const LiquidGlassCardContent = ({ children, className = "", ...props }) => (
  <div className={`p-6 pt-0 ${className}`} {...props}>
    {children}
  </div>
);

const LiquidGlassCardFooter = ({ children, className = "", ...props }) => (
  <div className={`flex items-center p-6 pt-0 ${className}`} {...props}>
    {children}
  </div>
);

export { 
  LiquidGlassCard, 
  LiquidGlassCardHeader, 
  LiquidGlassCardFooter, 
  LiquidGlassCardTitle, 
  LiquidGlassCardDescription, 
  LiquidGlassCardContent 
};
