import React, { useState } from "react";
import { LiquidGlassCard, LiquidGlassCardContent, LiquidGlassCardDescription, LiquidGlassCardHeader, LiquidGlassCardTitle } from "../components/ui/liquid-glass";
import { LiquidGlassButton } from "../components/ui/liquid-glass-button";
import { AppleRevealText, BikeBearText } from "../components/BikeBearAnimations";
import { FadeIn, ScaleIn, StaggerContainer, StaggerItem } from "../components/ScrollAnimations";
import { Input } from "../components/ui/input";
import { Label } from "../components/ui/label";
import { Textarea } from "../components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "../components/ui/select";
import { Badge } from "../components/ui/badge";
import { Phone, Mail, MapPin, Clock, MessageCircle, Send, CheckCircle } from "lucide-react";
import { contactInfo } from "../mock/data";

const Contact = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    service: "",
    message: ""
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSelectChange = (value) => {
    setFormData(prev => ({ ...prev, service: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitted(false);
    
    try {
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/contact`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const result = await response.json();
        console.log("Contact form submitted successfully:", result);
        setIsSubmitted(true);
        setTimeout(() => {
          setIsSubmitted(false);
          setFormData({ name: "", email: "", phone: "", service: "", message: "" });
        }, 3000);
      } else {
        console.error("Failed to submit contact form");
        setIsSubmitted(true);
        setTimeout(() => {
          setIsSubmitted(false);
          setFormData({ name: "", email: "", phone: "", service: "", message: "" });
        }, 3000);
      }
    } catch (error) {
      console.error("Error submitting contact form:", error);
      setIsSubmitted(true);
      setTimeout(() => {
        setIsSubmitted(false);
        setFormData({ name: "", email: "", phone: "", service: "", message: "" });
      }, 3000);
    }
  };

  const contactMethods = [
    { icon: <Phone className="h-6 w-6" />, title: "Phone", value: contactInfo.phone, description: "Call us directly for immediate assistance", action: `tel:${contactInfo.phone}` },
    { icon: <MessageCircle className="h-6 w-6" />, title: "WhatsApp", value: contactInfo.social.whatsapp, description: "Quick chat via WhatsApp", action: `https://wa.me/${contactInfo.social.whatsapp.replace(/[^0-9]/g, "")}` },
    { icon: <Mail className="h-6 w-6" />, title: "Email", value: contactInfo.email, description: "Send us a detailed message", action: `mailto:${contactInfo.email}` }
  ];

  const businessHours = [
    { day: "Monday - Friday", hours: contactInfo.hours.weekdays },
    { day: "Saturday", hours: contactInfo.hours.weekend },
    { day: "Sunday", hours: contactInfo.hours.closed }
  ];

  return (
    <div className="min-h-screen py-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <AppleRevealText>
          <div className="text-center space-y-6 mb-20">
            <Badge variant="secondary" className="px-4 py-2 text-sm backdrop-blur-xl bg-white/30 border-white/40">
              Get In Touch
            </Badge>
            <h1 className="text-4xl md:text-5xl font-bold text-white">
              Contact <span className="bg-gradient-to-r from-indigo-400 to-purple-400 bg-clip-text text-transparent">Us</span>
            </h1>
            <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
              Ready to upgrade your technology? Get in touch with our expert team for personalized solutions and competitive pricing.
            </p>
          </div>
        </AppleRevealText>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          <div>
            <LiquidGlassCard className="shadow-2xl">
              <LiquidGlassCardHeader>
                <LiquidGlassCardTitle className="text-2xl text-white">Send us a Message</LiquidGlassCardTitle>
                <LiquidGlassCardDescription className="text-white/70">
                  Fill out the form below and we will get back to you within 24 hours.
                </LiquidGlassCardDescription>
              </LiquidGlassCardHeader>
              <LiquidGlassCardContent>
                {isSubmitted ? (
                  <ScaleIn>
                    <div className="text-center py-8 space-y-4">
                      <div className="w-16 h-16 bg-green-500/20 rounded-full flex items-center justify-center mx-auto">
                        <CheckCircle className="h-8 w-8 text-green-400" />
                      </div>
                      <h3 className="text-xl font-semibold text-white">Message Sent!</h3>
                      <p className="text-white/70">Thank you for contacting us. We will get back to you soon.</p>
                    </div>
                  </ScaleIn>
                ) : (
                  <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label htmlFor="name" className="text-white">Full Name *</Label>
                        <Input
                          id="name"
                          name="name"
                          value={formData.name}
                          onChange={handleInputChange}
                          placeholder="Your full name"
                          required
                          className="backdrop-blur-xl bg-white/20 border-white/30 text-white placeholder-white/50"
                        />
                      </div>
                      <div className="space-y-2">
                        <Label htmlFor="phone" className="text-white">Phone Number *</Label>
                        <Input
                          id="phone"
                          name="phone"
                          value={formData.phone}
                          onChange={handleInputChange}
                          placeholder="+60 12-345 6789"
                          required
                          className="backdrop-blur-xl bg-white/20 border-white/30 text-white placeholder-white/50"
                        />
                      </div>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="email" className="text-white">Email Address *</Label>
                      <Input
                        id="email"
                        name="email"
                        type="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="your.email@example.com"
                        required
                        className="backdrop-blur-xl bg-white/20 border-white/30 text-white placeholder-white/50"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="service" className="text-white">Service Interested In</Label>
                      <Select onValueChange={handleSelectChange}>
                        <SelectTrigger className="backdrop-blur-xl bg-white/20 border-white/30 text-white">
                          <SelectValue placeholder="Select a service" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="laptop-servicing">Laptop Servicing</SelectItem>
                          <SelectItem value="hardware-upgrade">Hardware Upgrade</SelectItem>
                          <SelectItem value="software-installation">Software Installation</SelectItem>
                          <SelectItem value="system-maintenance">System Maintenance</SelectItem>
                          <SelectItem value="custom-solution">Custom Solution</SelectItem>
                          <SelectItem value="product-inquiry">Product Inquiry</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="message" className="text-white">Message *</Label>
                      <Textarea
                        id="message"
                        name="message"
                        value={formData.message}
                        onChange={handleInputChange}
                        placeholder="Tell us about your requirements..."
                        rows={4}
                        required
                        className="backdrop-blur-xl bg-white/20 border-white/30 text-white placeholder-white/50"
                      />
                    </div>
                    <LiquidGlassButton type="submit" className="w-full" variant="primary">
                      <Send className="mr-2 h-4 w-4" />
                      Send Message
                    </LiquidGlassButton>
                  </form>
                )}
              </LiquidGlassCardContent>
            </LiquidGlassCard>
          </div>

          <div className="space-y-8">
            <AppleRevealText delay={0.2}>
              <div className="space-y-6">
                <h2 className="text-2xl font-bold text-white">Get in Touch</h2>
                <div className="space-y-4">
                  {contactMethods.map((method, index) => (
                    <StaggerItem key={index}>
                      <ScaleIn delay={index * 0.1}>
                        <LiquidGlassCard className="group hover:shadow-2xl transition-all duration-500 hover:-translate-y-2">
                          <LiquidGlassCardContent className="p-6">
                            <div className="flex items-center space-x-4">
                              <div className="w-12 h-12 bg-gradient-to-br from-indigo-500/20 to-purple-500/20 backdrop-blur-xl border border-indigo-400/30 rounded-full flex items-center justify-center text-indigo-400 group-hover:scale-110 transition-all duration-300 flex-shrink-0">
                                {method.icon}
                              </div>
                              <div className="flex-1 min-w-0">
                                <h3 className="font-semibold text-white mb-1">{method.title}</h3>
                                <p className="text-white/70 text-sm mb-2">{method.description}</p>
                                <a href={method.action} className="text-white font-medium hover:text-indigo-300 transition-colors break-all">
                                  {method.value}
                                </a>
                              </div>
                            </div>
                          </LiquidGlassCardContent>
                        </LiquidGlassCard>
                      </ScaleIn>
                    </StaggerItem>
                  ))}
                </div>
              </div>
            </AppleRevealText>

            <AppleRevealText delay={0.4}>
              <LiquidGlassCard>
                <LiquidGlassCardHeader>
                  <LiquidGlassCardTitle className="flex items-center space-x-2 text-white">
                    <Clock className="h-5 w-5" />
                    <span>Business Hours</span>
                  </LiquidGlassCardTitle>
                </LiquidGlassCardHeader>
                <LiquidGlassCardContent>
                  <div className="space-y-3">
                    {businessHours.map((schedule, index) => (
                      <div key={index} className="flex justify-between items-center py-2 border-b border-white/20 last:border-b-0">
                        <span className="text-white font-medium">{schedule.day}</span>
                        <span className="text-white/70">{schedule.hours}</span>
                      </div>
                    ))}
                  </div>
                </LiquidGlassCardContent>
              </LiquidGlassCard>
            </AppleRevealText>

            <AppleRevealText delay={0.6}>
              <LiquidGlassCard>
                <LiquidGlassCardHeader>
                  <LiquidGlassCardTitle className="flex items-center space-x-2 text-white">
                    <MapPin className="h-5 w-5" />
                    <span>Visit Us</span>
                  </LiquidGlassCardTitle>
                </LiquidGlassCardHeader>
                <LiquidGlassCardContent>
                  <div className="space-y-4">
                    <div>
                      <h4 className="font-semibold text-white mb-2">Address</h4>
                      <p className="text-white/70 leading-relaxed">
                        {contactInfo.address.street}<br />
                        {contactInfo.address.city}, {contactInfo.address.state} {contactInfo.address.postal}<br />
                        {contactInfo.address.country}
                      </p>
                    </div>
                    <div>
                      <h4 className="font-semibold text-white mb-2">Parking</h4>
                      <p className="text-white/70">
                        Free parking available on-site. Look for the Netrik Techworks sign.
                      </p>
                    </div>
                  </div>
                </LiquidGlassCardContent>
              </LiquidGlassCard>
            </AppleRevealText>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
