import React, { useState, useEffect } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { GlassCard, GlassCardHeader, GlassCardContent, GlassCardTitle } from "../../components/ui/glass-card";
import { 
  MessageSquare, 
  FileText, 
  FolderOpen, 
  Star,
  TrendingUp,
  Users,
  Calendar,
  Activity
} from "lucide-react";

const AdminOverview = ({ adminToken }) => {
  const [stats, setStats] = useState({
    contacts: 0,
    quotations: 0,
    projects: 0,
    testimonials: 0,
    recentContacts: [],
    recentQuotations: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOverviewData();
  }, [adminToken]);

  const fetchOverviewData = async () => {
    try {
      const headers = { Authorization: `Bearer ${adminToken}` };
      
      const [contactsRes, quotationsRes, projectsRes, testimonialsRes] = await Promise.all([
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/admin/contact-submissions`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/admin/quotations`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/admin/projects`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/api/admin/testimonials`, { headers })
      ]);

      setStats({
        contacts: contactsRes.data.length,
        quotations: quotationsRes.data.length,
        projects: projectsRes.data.length,
        testimonials: testimonialsRes.data.length,
        recentContacts: contactsRes.data.slice(0, 5),
        recentQuotations: quotationsRes.data.slice(0, 5)
      });
      setLoading(false);
    } catch (error) {
      console.error('Error fetching overview data:', error);
      setLoading(false);
    }
  };

  const statCards = [
    {
      title: "Contact Submissions",
      value: stats.contacts,
      icon: MessageSquare,
      color: "text-blue-600",
      bgColor: "bg-blue-50",
      change: "+12%"
    },
    {
      title: "Quotations",
      value: stats.quotations,
      icon: FileText,
      color: "text-green-600",
      bgColor: "bg-green-50",
      change: "+8%"
    },
    {
      title: "Projects",
      value: stats.projects,
      icon: FolderOpen,
      color: "text-purple-600",
      bgColor: "bg-purple-50",
      change: "+5%"
    },
    {
      title: "Testimonials",
      value: stats.testimonials,
      icon: Star,
      color: "text-yellow-600",
      bgColor: "bg-yellow-50",
      change: "+15%"
    }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center py-20">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-slate-900"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-gradient-to-r from-slate-900 to-slate-700 rounded-2xl p-8 text-white"
      >
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">Welcome back, Admin!</h1>
            <p className="text-slate-300 text-lg">
              Here's what's happening with Netrik Techworks today.
            </p>
          </div>
          <div className="hidden md:block">
            <Activity className="w-16 h-16 text-slate-400" />
          </div>
        </div>
      </motion.div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <motion.div
              key={stat.title}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <GlassCard className="hover:shadow-xl transition-all duration-300">
                <GlassCardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-slate-600 mb-1">{stat.title}</p>
                      <p className="text-3xl font-bold text-slate-900">{stat.value}</p>
                      <div className="flex items-center mt-2">
                        <TrendingUp className="w-4 h-4 text-green-500 mr-1" />
                        <span className="text-sm text-green-600">{stat.change}</span>
                      </div>
                    </div>
                    <div className={`w-12 h-12 ${stat.bgColor} rounded-lg flex items-center justify-center`}>
                      <Icon className={`w-6 h-6 ${stat.color}`} />
                    </div>
                  </div>
                </GlassCardContent>
              </GlassCard>
            </motion.div>
          );
        })}
      </div>

      {/* Recent Activities */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Contacts */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.5 }}
        >
          <GlassCard>
            <GlassCardHeader>
              <GlassCardTitle className="flex items-center space-x-2">
                <MessageSquare className="w-5 h-5" />
                <span>Recent Contact Submissions</span>
              </GlassCardTitle>
            </GlassCardHeader>
            <GlassCardContent>
              {stats.recentContacts.length > 0 ? (
                <div className="space-y-4">
                  {stats.recentContacts.map((contact) => (
                    <div key={contact.id} className="flex items-center space-x-3 p-3 bg-slate-50 rounded-lg">
                      <div className="w-10 h-10 bg-slate-200 rounded-full flex items-center justify-center">
                        <Users className="w-5 h-5 text-slate-600" />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-slate-900 truncate">{contact.name}</p>
                        <p className="text-sm text-slate-600 truncate">{contact.service}</p>
                        <div className="flex items-center text-xs text-slate-500 mt-1">
                          <Calendar className="w-3 h-3 mr-1" />
                          {new Date(contact.submitted_at).toLocaleDateString()}
                        </div>
                      </div>
                      {!contact.is_read && (
                        <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                      )}
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-slate-500 text-center py-8">No recent contacts</p>
              )}
            </GlassCardContent>
          </GlassCard>
        </motion.div>

        {/* Recent Quotations */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.6 }}
        >
          <GlassCard>
            <GlassCardHeader>
              <GlassCardTitle className="flex items-center space-x-2">
                <FileText className="w-5 h-5" />
                <span>Recent Quotations</span>
              </GlassCardTitle>
            </GlassCardHeader>
            <GlassCardContent>
              {stats.recentQuotations.length > 0 ? (
                <div className="space-y-4">
                  {stats.recentQuotations.map((quotation) => (
                    <div key={quotation.id} className="flex items-center space-x-3 p-3 bg-slate-50 rounded-lg">
                      <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
                        <FileText className="w-5 h-5 text-green-600" />
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="font-medium text-slate-900 truncate">{quotation.quote_number}</p>
                        <p className="text-sm text-slate-600 truncate">{quotation.client_name}</p>
                        <div className="flex items-center justify-between text-xs text-slate-500 mt-1">
                          <div className="flex items-center">
                            <Calendar className="w-3 h-3 mr-1" />
                            {new Date(quotation.created_at).toLocaleDateString()}
                          </div>
                          <span className="font-medium text-slate-900">
                            RM {quotation.total_amount.toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-slate-500 text-center py-8">No recent quotations</p>
              )}
            </GlassCardContent>
          </GlassCard>
        </motion.div>
      </div>

      {/* Quick Actions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.7 }}
      >
        <GlassCard>
          <GlassCardHeader>
            <GlassCardTitle>Quick Actions</GlassCardTitle>
          </GlassCardHeader>
          <GlassCardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {[
                { label: "New Quotation", icon: FileText, href: "/admin/quotations" },
                { label: "Add Project", icon: FolderOpen, href: "/admin/projects" },
                { label: "Add Testimonial", icon: Star, href: "/admin/testimonials" },
                { label: "View Contacts", icon: MessageSquare, href: "/admin/contacts" }
              ].map((action) => {
                const Icon = action.icon;
                return (
                  <button
                    key={action.label}
                    onClick={() => window.location.href = action.href}
                    className="p-4 text-center bg-slate-50 hover:bg-slate-100 rounded-lg transition-colors group"
                  >
                    <Icon className="w-8 h-8 mx-auto mb-2 text-slate-600 group-hover:text-slate-900" />
                    <p className="text-sm font-medium text-slate-700 group-hover:text-slate-900">
                      {action.label}
                    </p>
                  </button>
                );
              })}
            </div>
          </GlassCardContent>
        </GlassCard>
      </motion.div>
    </div>
  );
};

export default AdminOverview;
