import React, { useEffect, useRef, useState } from "react";

const CursorReactiveBackground = () => {
  const canvasRef = useRef(null);
  const animationRef = useRef(null);
  const dotsRef = useRef([]);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    
    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // Create dots
    const dotCount = 1500;
    const dots = [];
    
    for (let i = 0; i < dotCount; i++) {
      dots.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        originalX: Math.random() * canvas.width,
        originalY: Math.random() * canvas.height,
        size: 1.5,
        originalSize: 1.5,
        opacity: 0.4,
        originalOpacity: 0.4,
        velocity: {
          x: (Math.random() - 0.5) * 0.3,
          y: (Math.random() - 0.5) * 0.3
        }
      });
    }
    
    dotsRef.current = dots;

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      dotsRef.current.forEach(dot => {
        // Calculate distance from mouse
        const dx = mousePosition.x - dot.x;
        const dy = mousePosition.y - dot.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const maxDistance = 120;

        if (distance < maxDistance && isHovering) {
          // Repel effect
          const force = (maxDistance - distance) / maxDistance;
          const angle = Math.atan2(dy, dx);
          
          dot.x -= Math.cos(angle) * force * 4;
          dot.y -= Math.sin(angle) * force * 4;
          
          // Scale and opacity effects
          dot.size = dot.originalSize + force * 2;
          dot.opacity = Math.min(1, dot.originalOpacity + force * 0.6);
        } else {
          // Return to original position
          dot.x += (dot.originalX - dot.x) * 0.08;
          dot.y += (dot.originalY - dot.y) * 0.08;
          dot.size += (dot.originalSize - dot.size) * 0.08;
          dot.opacity += (dot.originalOpacity - dot.opacity) * 0.08;
        }

        // Add subtle floating motion
        dot.x += dot.velocity.x;
        dot.y += dot.velocity.y;

        // Bounce off edges
        if (dot.x < 0 || dot.x > canvas.width) {
          dot.velocity.x *= -1;
          dot.x = Math.max(0, Math.min(canvas.width, dot.x));
        }
        if (dot.y < 0 || dot.y > canvas.height) {
          dot.velocity.y *= -1;
          dot.y = Math.max(0, Math.min(canvas.height, dot.y));
        }

        // Draw dot
        ctx.beginPath();
        ctx.arc(dot.x, dot.y, dot.size, 0, Math.PI * 2);
        ctx.fillStyle = `rgba(255, 255, 255, ${dot.opacity})`;
        ctx.fill();
      });

      animationRef.current = requestAnimationFrame(animate);
    };

    // Start animation
    animate();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
      window.removeEventListener('resize', resizeCanvas);
    };
  }, [mousePosition, isHovering]);

  const handleMouseMove = (e) => {
    setMousePosition({
      x: e.clientX,
      y: e.clientY
    });
  };

  const handleMouseEnter = () => {
    setIsHovering(true);
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  return (
    <div 
      className="fixed inset-0 pointer-events-none z-0"
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <canvas
        ref={canvasRef}
        className="w-full h-full"
        style={{ 
          background: 'transparent',
          width: '100%',
          height: '100%'
        }}
      />
    </div>
  );
};

export default CursorReactiveBackground;
