import React, { useState, useEffect } from "react";
import { Routes, Route, Navigate, useNavigate, Link, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import { 
  LayoutDashboard, 
  MessageSquare, 
  FileText, 
  FolderOpen, 
  Star, 
  LogOut,
  User,
  Menu,
  X
} from "lucide-react";

// Import admin components
import AdminLogin from "./AdminLogin";
import AdminOverview from "./AdminOverview";
import ContactSubmissions from "./ContactSubmissions";
import QuotationManager from "./QuotationManager";
import ProjectManager from "./ProjectManager";
import TestimonialManager from "./TestimonialManager";

const AdminDashboard = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [adminToken, setAdminToken] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Clear any existing token and force login
    localStorage.removeItem('adminToken');
    setIsAuthenticated(false);
    setAdminToken(null);
    setIsLoading(false);
  }, []);

  const handleLogin = (token) => {
    localStorage.setItem('adminToken', token);
    setAdminToken(token);
    setIsAuthenticated(true);
    navigate('/a7k-9m2-x4n-8p1-q5r/overview');
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    setAdminToken(null);
    setIsAuthenticated(false);
    navigate('/a7k-9m2-x4n-8p1-q5r');
  };

  const navigationItems = [
    { name: "Overview", path: "/a7k-9m2-x4n-8p1-q5r/overview", icon: LayoutDashboard },
    { name: "Contact Submissions", path: "/a7k-9m2-x4n-8p1-q5r/contact-submissions", icon: MessageSquare },
    { name: "Quotations", path: "/a7k-9m2-x4n-8p1-q5r/quotations", icon: FileText },
    { name: "Projects", path: "/a7k-9m2-x4n-8p1-q5r/projects", icon: FolderOpen },
    { name: "Testimonials", path: "/a7k-9m2-x4n-8p1-q5r/testimonials", icon: Star },
  ];

  const isActive = (path) => location.pathname === path;

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
      </div>
    );
  }

  if (!isAuthenticated) {
    return <AdminLogin onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Sidebar */}
      <motion.div
        initial={{ x: -300 }}
        animate={{ x: sidebarOpen ? 0 : -300 }}
        transition={{ duration: 0.3, ease: "easeInOut" }}
        className="fixed inset-y-0 left-0 z-50 w-56 bg-white/10 backdrop-blur-xl border-r border-white/20 lg:translate-x-0 lg:static lg:inset-0 lg:w-56"
      >
        <div className="flex flex-col h-full">
          {/* Logo */}
          <div className="flex items-center justify-between h-16 px-6 border-b border-white/20">
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-gradient-to-br from-indigo-500 to-purple-500 rounded-lg flex items-center justify-center">
                <User className="h-5 w-5 text-white" />
              </div>
              <span className="text-xl font-bold text-white">Admin Panel</span>
            </div>
            <button
              onClick={() => setSidebarOpen(false)}
              className="lg:hidden p-2 rounded-lg text-white/70 hover:bg-white/10 transition-colors"
            >
              <X className="h-5 w-5" />
            </button>
          </div>

          {/* Navigation */}
          <nav className="flex-1 px-4 py-6 space-y-2">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.name}
                  to={item.path}
                  onClick={() => setSidebarOpen(false)}
                  className={`flex items-center space-x-3 px-4 py-3 rounded-lg text-sm font-medium transition-all duration-200 ${
                    isActive(item.path)
                      ? "bg-white/20 text-white border border-white/30"
                      : "text-white/70 hover:bg-white/10 hover:text-white"
                  }`}
                >
                  <Icon className="h-5 w-5" />
                  <span>{item.name}</span>
                </Link>
              );
            })}
          </nav>

          {/* Logout Button */}
          <div className="p-4 border-t border-white/20">
            <button
              onClick={handleLogout}
              className="flex items-center space-x-3 w-full px-4 py-3 rounded-lg text-sm font-medium text-white/70 hover:bg-red-500/20 hover:text-red-300 transition-all duration-200"
            >
              <LogOut className="h-5 w-5" />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </motion.div>

      {/* Main Content */}
      <div className="lg:ml-56">
        {/* Top Bar */}
        <header className="bg-white/10 backdrop-blur-xl border-b border-white/20 px-6 py-4">
          <div className="flex items-center justify-between">
            <button
              onClick={() => setSidebarOpen(true)}
              className="lg:hidden p-2 rounded-lg text-white/70 hover:bg-white/10 transition-colors"
            >
              <Menu className="h-5 w-5" />
            </button>
            <div className="flex items-center space-x-4">
              <div className="text-sm text-white/70">
                Welcome back, Admin
              </div>
              <div className="w-8 h-8 bg-gradient-to-br from-indigo-500 to-purple-500 rounded-full flex items-center justify-center">
                <User className="h-4 w-4 text-white" />
              </div>
            </div>
          </div>
        </header>

        {/* Page Content */}
        <main className="p-6">
          <Routes>
            <Route path="/" element={<Navigate to="/a7k-9m2-x4n-8p1-q5r/overview" replace />} />
            <Route path="/overview" element={<AdminOverview />} />
            <Route path="/contact-submissions" element={<ContactSubmissions />} />
            <Route path="/quotations" element={<QuotationManager />} />
            <Route path="/projects" element={<ProjectManager />} />
            <Route path="/testimonials" element={<TestimonialManager />} />
          </Routes>
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
