import React from 'react';
import { cn } from '../../lib/utils';

const GlassCard = React.forwardRef(({ className, children, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "backdrop-blur-xl bg-white/10 border border-white/20 shadow-2xl rounded-2xl relative overflow-hidden",
      "before:absolute before:inset-0 before:bg-gradient-to-br before:from-white/20 before:to-transparent before:pointer-events-none",
      "after:absolute after:inset-0 after:bg-gradient-to-t after:from-black/5 after:to-transparent after:pointer-events-none",
      className
    )}
    {...props}
  >
    <div className="relative z-10">
      {children}
    </div>
  </div>
));

GlassCard.displayName = "GlassCard";

const GlassCardHeader = React.forwardRef(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex flex-col space-y-1.5 p-6", className)}
    {...props}
  />
));

GlassCardHeader.displayName = "GlassCardHeader";

const GlassCardTitle = React.forwardRef(({ className, ...props }, ref) => (
  <h3
    ref={ref}
    className={cn("text-2xl font-semibold leading-none tracking-tight text-slate-900", className)}
    {...props}
  />
));

GlassCardTitle.displayName = "GlassCardTitle";

const GlassCardDescription = React.forwardRef(({ className, ...props }, ref) => (
  <p
    ref={ref}
    className={cn("text-sm text-slate-600", className)}
    {...props}
  />
));

GlassCardDescription.displayName = "GlassCardDescription";

const GlassCardContent = React.forwardRef(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6 pt-0", className)} {...props} />
));

GlassCardContent.displayName = "GlassCardContent";

const GlassCardFooter = React.forwardRef(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex items-center p-6 pt-0", className)}
    {...props}
  />
));

GlassCardFooter.displayName = "GlassCardFooter";

export { GlassCard, GlassCardHeader, GlassCardFooter, GlassCardTitle, GlassCardDescription, GlassCardContent };
