import React, { useState, useRef } from "react";
import { motion } from "framer-motion";

const LiquidGlassCard = ({ children, className = "", hover = true, ...props }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    if (!cardRef.current) return;
    
    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    setMousePosition({ x, y });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setMousePosition({ x: 0, y: 0 });
  };

  // Calculate transform values for 3D liquid effect
  const rotateX = isHovered ? (mousePosition.y - 150) / 15 : 0;
  const rotateY = isHovered ? (mousePosition.x - 200) / 15 : 0;
  const translateZ = isHovered ? 20 : 0;

  return (
    <motion.div
      ref={cardRef}
      className={`relative group overflow-hidden rounded-3xl border border-white/30 shadow-2xl shadow-black/20 
        transition-all duration-700 ease-out ${className}`}
      whileHover={hover ? { 
        scale: 1.02, 
        y: -8, 
        transition: { 
          type: "spring", 
          stiffness: 200, 
          damping: 25
        } 
      } : {}}
      whileTap={hover ? { 
        scale: 0.98, 
        y: -4,
        transition: { 
          type: "spring", 
          stiffness: 300, 
          damping: 20 
        } 
      } : {}}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
      onMouseMove={handleMouseMove}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      style={{
        transform: `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateZ(${translateZ}px)`,
        background: isHovered 
          ? `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.05) 50%, transparent 70%)`
          : 'rgba(255, 255, 255, 0.05)',
        backdropFilter: isHovered 
          ? `blur(${20 + Math.abs(rotateX) + Math.abs(rotateY)}px) saturate(180%)`
          : 'blur(20px) saturate(120%)',
      }}
      {...props}
    >
      {/* Liquid glass distortion layer */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 1 : 0,
          background: isHovered ? 
            `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0.1) 40%, transparent 70%)` :
            "transparent",
          backdropFilter: isHovered 
            ? `blur(${10 + Math.abs(rotateX) * 2}px) saturate(200%)`
            : 'blur(0px)',
        }}
        transition={{ duration: 0.1 }}
      />
      
      {/* Liquid ripple effect */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? [0, 1, 0] : 0,
          background: isHovered ? 
            `radial-gradient(circle at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.3) 0%, transparent 50%)` :
            "transparent",
          backdropFilter: isHovered 
            ? `blur(${5 + Math.abs(rotateY) * 2}px)`
            : 'blur(0px)',
        }}
        transition={{ duration: 0.6, ease: "easeOut" }}
      />
      
      {/* Liquid distortion waves */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 0.6 : 0,
          background: isHovered ? 
            `conic-gradient(from ${mousePosition.x}deg at ${mousePosition.x}px ${mousePosition.y}px, transparent 0deg, rgba(255, 255, 255, 0.1) 90deg, transparent 180deg, rgba(255, 255, 255, 0.1) 270deg, transparent 360deg)` :
            "transparent",
          backdropFilter: isHovered 
            ? `blur(${8 + Math.abs(rotateX + rotateY)}px) saturate(150%)`
            : 'blur(0px)',
        }}
        transition={{ duration: 0.3, ease: "easeOut" }}
      />
      
      {/* Liquid glass refraction */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 0.8 : 0,
          background: isHovered ? 
            `linear-gradient(${45 + rotateY * 2}deg, transparent 0%, rgba(255, 255, 255, 0.1) 25%, rgba(255, 255, 255, 0.2) 50%, rgba(255, 255, 255, 0.1) 75%, transparent 100%)` :
            "transparent",
          backdropFilter: isHovered 
            ? `blur(${12 + Math.abs(rotateX) * 1.5}px) saturate(180%)`
            : 'blur(0px)',
        }}
        transition={{ duration: 0.2, ease: "easeOut" }}
      />
      
      {/* Liquid glass surface tension */}
      <motion.div
        className="absolute inset-0 opacity-0"
        animate={{
          opacity: isHovered ? 1 : 0,
          background: isHovered ? 
            `radial-gradient(ellipse at ${mousePosition.x}px ${mousePosition.y}px, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0.05) 30%, transparent 60%)` :
            "transparent",
          backdropFilter: isHovered 
            ? `blur(${6 + Math.abs(rotateY) * 1.2}px) saturate(160%)`
            : 'blur(0px)',
        }}
        transition={{ duration: 0.15, ease: "easeOut" }}
      />
      
      {/* Content with liquid glass distortion */}
      <motion.div 
        className="relative z-10"
        animate={{
          filter: isHovered 
            ? `blur(${Math.abs(rotateX + rotateY) * 0.5}px) saturate(${120 + Math.abs(rotateX + rotateY) * 2}%)`
            : 'blur(0px) saturate(100%)',
        }}
        transition={{ duration: 0.1 }}
      >
        {children}
      </motion.div>
    </motion.div>
  );
};

const LiquidGlassCardHeader = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex flex-col space-y-1.5 p-6 ${className}`} 
    whileHover={{ y: -2, scale: 1.005 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardTitle = ({ children, className = "", ...props }) => (
  <motion.h3 
    className={`text-2xl font-semibold leading-none tracking-tight ${className}`} 
    whileHover={{ scale: 1.01, y: -1 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.h3>
);

const LiquidGlassCardDescription = ({ children, className = "", ...props }) => (
  <motion.p 
    className={`text-sm text-muted-foreground ${className}`} 
    whileHover={{ y: -1, scale: 1.005 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.p>
);

const LiquidGlassCardContent = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`p-6 pt-0 ${className}`} 
    whileHover={{ y: -2, scale: 1.005 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

const LiquidGlassCardFooter = ({ children, className = "", ...props }) => (
  <motion.div 
    className={`flex items-center p-6 pt-0 ${className}`} 
    whileHover={{ y: -2, scale: 1.005 }}
    transition={{ duration: 0.2 }}
    {...props}
  >
    {children}
  </motion.div>
);

export { 
  LiquidGlassCard, 
  LiquidGlassCardHeader, 
  LiquidGlassCardFooter, 
  LiquidGlassCardTitle, 
  LiquidGlassCardDescription, 
  LiquidGlassCardContent 
};
